#find and replace "username" to your target user
Get-ChildItem -Directory -Recurse -Depth 1 -Force  C:\ -ErrorAction SilentlyContinue | where {(get-acl $_.fullname).access | where {($_.identityreference -eq "$env:computername\username") -or ($_.identityreference -eq 'nt authority\authenticated users') -or ($_.identityreference -eq 'builtin\users') -or ($_.identityreference -eq "$env:computername\Users") -and ($_.filesystemrights -eq 'FullControl') -or ($_.filesystemrights -eq 'Modify') -or ($_.filesystemrights -eq 'Write')}}
